import { useEffect } from "react";
import { useState } from "react";
import "./newRoom.css";

const NewRoom = () => {
  const [profile, setProfile] = useState({
    title: "",
    desc: "",
    price: "",
    maxPeople: "",
    roomNumbers: "",
  });
  const [idHotel, setIdHotel] = useState("");
  const [hotel, setHotel] = useState([]);
  const validate = () => {
    // if (profile.name === "") {
    //   return { ok: false, message: "Please fill in name!!" };
    // } else if (profile.type === "") {
    //   return { ok: false, message: "Please fill in type!!" };
    // } else if (profile.city === "") {
    //   return { ok: false, message: "Please fill in city!!" };
    // } else if (profile.address === "") {
    //   return { ok: false, message: "Please fill in address!!" };
    // } else if (profile.distance === "") {
    //   return { ok: false, message: "Please fill in distance!!" };
    // } else if (profile.title === "") {
    //   return { ok: false, message: "Please fill in title!!" };
    // } else if (profile.cheapestPrice === "") {
    //   return { ok: false, message: "Please fill in price!!" };
    // } else if (profile.photos === "") {
    //   return { ok: false, message: "Please fill in images!!" };
    // } else if (profile.rooms === "") {
    //   return { ok: false, message: "Please fill in rooms!!" };
    // } else {
    //   return { ok: true };
    // }
  };
  const handleSubmit = () => {
    console.log(profile);
    // const vadidateForm = validate();
    // if (!vadidateForm.ok) {
    //   alert(vadidateForm.message);
    // } else {
    //   fetch("http://localhost:5000/admin/add-hotel", {
    //     method: "POST",
    //     body: JSON.stringify(profile),
    //     headers: { "Content-Type": "application/json" },
    //     credentials: "same-origin",
    //   })
    //     .then((reponse) => {
    //       return reponse.json();
    //     })
    //     .then((reponse) => {
    //       if (reponse.ok) {
    //         alert("Successfull!");
    //         setProfile({
    //           name: "",
    //           type: "",
    //           city: "",
    //           address: "",
    //           distance: "",
    //           title: "",
    //           desc: "",
    //           cheapestPrice: "",
    //           photos: "",
    //           featured: false,
    //           rooms: "",
    //         });
    //       }
    //     })
    //     .catch((err) => console.log(err));
    // }
  };

  useEffect(() => {
    fetch("http://localhost:5000/admin/get-hotels")
      .then((reponse) => {
        return reponse.json();
      })
      .then((reponse) => {
        console.log(reponse);
        setHotel(reponse.arr);
      });
  }, []);
  return (
    <div className="col-10 main">
      <div className="title">Add New Rooom</div>
      <div className="form-add row">
        <div className="col-6 input">
          <label htmlFor="title">Title</label>
          <input
            id="title"
            name="title"
            type="text"
            placeholder="2 bed room"
            value={profile.title}
            onChange={(evt) => {
              setProfile({ ...profile, title: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="desc">Description</label>
          <input
            id="desc"
            name="desc"
            type="text"
            placeholder="King size bed,1 bathroom"
            value={profile.desc}
            onChange={(evt) => {
              setProfile({ ...profile, desc: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="price">Price</label>
          <input
            id="price"
            name="price"
            type="text"
            placeholder="100"
            value={profile.price}
            onChange={(evt) => {
              setProfile({ ...profile, price: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="maxPeople">Max People</label>
          <input
            id="maxPeople"
            name="maxPeople"
            type="text"
            placeholder="elton st, 216"
            value={profile.maxPeople}
            onChange={(evt) => {
              setProfile({ ...profile, maxPeople: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="roomNumbers">Rooms</label>
          <textarea
            id="roomNumbers"
            name="roomNumbers"
            value={profile.roomNumbers}
            placeholder="give comma between room numbers."
            onChange={(evt) => {
              setProfile({ ...profile, roomNumbers: evt.target.value });
            }}
          ></textarea>
        </div>
        <div className="col-6 input">
          <label htmlFor="hotel">Choose a hotel</label>
          <select
            onChange={(evt) => {
              if (evt.target.value === "Yes") {
                setProfile({ ...profile, featured: true });
              } else {
                setProfile({ ...profile, featured: false });
              }
            }}
          >
            {hotel.map((h) => {
              return <option value={h._id}>{h.name}</option>;
            })}
          </select>
        </div>
        <div className="button-send">
          <button type="submit" onClick={handleSubmit}>
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default NewRoom;
