import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSquare } from "@fortawesome/free-solid-svg-icons";
import "./hotels.css";
import { useEffect, useState } from "react";

const Hotel = () => {
  const [check, setCheck] = useState(false);
  const trans = 8;
  const [page, setPage] = useState(1);
  const [list, setList] = useState([]);
  const [maxPage, setMaxPage] = useState(1);
  const handleDelete = (t) => {
    if (!window.confirm("Do you want delete this Hotel?")) return;
    fetch("http://localhost:5000/admin/delete-hotel", {
      method: "POST",
      body: JSON.stringify({ id: t._id }),
      headers: { "Content-Type": "application/json" },
      credentials: "same-origin",
    })
      .then((reponse) => {
        return reponse.json();
      })
      .then((reponse) => {
        if (reponse.check) {
          alert("Successfull!");
          setCheck(true);
        } else {
          alert("Can not delete this Hotel!");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };
  useEffect(() => {
    fetch("http://localhost:5000/admin/get-hotels", {
      method: "POST",
      body: JSON.stringify({ page: page, trans: trans }),
      headers: { "Content-Type": "application/json" },
      credentials: "same-origin",
    })
      .then((reponse) => {
        return reponse.json();
      })
      .then((reponse) => {
        setList(reponse.arr);
        setMaxPage(reponse.maxPage);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [page]);
  if (check) {
    return <Navigate to={`/hotel`} />;
  }
  return (
    <div className="transactions-box">
      <h1>Hotels List</h1>
      <div className="t-box">
        <table className="table">
          <thead>
            <tr>
              <th scope="col">
                <FontAwesomeIcon icon={faSquare} />
              </th>
              <th scope="col">id</th>
              <th scope="col">Name</th>
              <th scope="col">Type</th>
              <th scope="col">Title</th>
              <th scope="col">City</th>
              <th scope="col">Action</th>
            </tr>
          </thead>
          <tbody>
            {list.map((t) => {
              return (
                <tr key={t._id}>
                  <th scope="col">
                    <FontAwesomeIcon icon={faSquare} />
                  </th>
                  <td>{t._id}</td>
                  <td>{t.name}</td>
                  <td>{t.type}</td>
                  <td>{t.title}</td>
                  <td>{t.city}</td>
                  <td>
                    <button
                      className="btn-delete"
                      onClick={() => handleDelete(t)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
        <div className="space"></div>
        <div className="page">
          <div>
            <p>
              {page}-{maxPage}
            </p>
            {page > 1 ? (
              <button
                onClick={() => {
                  setPage(page - 1);
                }}
              >
                {"<"}
              </button>
            ) : (
              <div></div>
            )}
            {page < maxPage ? (
              <button
                onClick={() => {
                  setPage(page + 1);
                }}
              >
                {">"}
              </button>
            ) : (
              <div></div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Hotel;
