import { useState } from "react";
import "./newHotel.css";

const NewHotel = () => {
  const [profile, setProfile] = useState({
    name: "",
    type: "",
    city: "",
    address: "",
    distance: "",
    title: "",
    desc: "",
    cheapestPrice: "",
    images: "",
    featured: false,
    rooms: "",
  });
  const validate = () => {
    if (profile.name === "") {
      return { ok: false, message: "Please fill in name!!" };
    } else if ((profile.type = "")) {
      return { ok: false, message: "Please fill in type!!" };
    } else if ((profile.city = "")) {
      return { ok: false, message: "Please fill in city!!" };
    } else if ((profile.address = "")) {
      return { ok: false, message: "Please fill in address!!" };
    } else if ((profile.distance = "")) {
      return { ok: false, message: "Please fill in distance!!" };
    } else if ((profile.title = "")) {
      return { ok: false, message: "Please fill in title!!" };
    } else if ((profile.cheapestPrice = "")) {
      return { ok: false, message: "Please fill in price!!" };
    } else if ((profile.images = "")) {
      return { ok: false, message: "Please fill in images!!" };
    } else if ((profile.rooms = "")) {
      return { ok: false, message: "Please fill in rooms!!" };
    }
  };
  const handleSubmit = () => {
    console.log(profile);
  };
  return (
    <div className="col-10 main">
      <div className="title">Add New Product</div>
      <div className="form-add row">
        <div className="col-6 input">
          <label htmlFor="name">Name</label>
          <input
            id="name"
            name="name"
            type="text"
            placeholder="My Hotel"
            onChange={(evt) => {
              setProfile({ ...profile, name: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="type">Type</label>
          <input
            id="type"
            name="type"
            type="text"
            placeholder="hotel"
            onChange={(evt) => {
              setProfile({ ...profile, type: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="distance">City</label>
          <input
            id="distance"
            name="distance"
            type="text"
            placeholder="500"
            onChange={(evt) => {
              setProfile({ ...profile, city: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="adress">Address</label>
          <input
            id="adress"
            name="adress"
            type="text"
            placeholder="elton st, 216"
            onChange={(evt) => {
              setProfile({ ...profile, address: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="city">Distance from City Center</label>
          <input
            id="city"
            name="city"
            type="text"
            placeholder="500"
            onChange={(evt) => {
              setProfile({ ...profile, distance: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="title">Title</label>
          <input
            id="title"
            name="title"
            type="text"
            placeholder="The best Hotel"
            onChange={(evt) => {
              setProfile({ ...profile, title: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="desc">Description</label>
          <input
            id="desc"
            name="desc"
            type="text"
            placeholder="description"
            onChange={(evt) => {
              setProfile({ ...profile, desc: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="price">Price</label>
          <input
            id="price"
            name="price"
            type="text"
            placeholder="100"
            onChange={(evt) => {
              setProfile({ ...profile, cheapestPrice: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="images">Images</label>
          <textarea
            id="images"
            name="images"
            onChange={(evt) => {
              setProfile({ ...profile, images: evt.target.value });
            }}
          ></textarea>
        </div>
        <div className="col-6 input">
          <label htmlFor="featured">Featured</label>
          <select
            onChange={(evt) => {
              if (evt.target.value === "Yes") {
                setProfile({ ...profile, featured: true });
              } else {
                setProfile({ ...profile, featured: false });
              }
            }}
          >
            <option>Yes</option>
            <option>No</option>
          </select>
        </div>
        <div className="col-12 input">
          <label htmlFor="rooms">Rooms</label>
          <textarea
            id="rooms"
            name="rooms"
            onChange={(evt) => {
              setProfile({ ...profile, rooms: evt.target.value });
            }}
          ></textarea>
        </div>
        <div className="button-send">
          <button type="submit" onClick={handleSubmit}>
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default NewHotel;
