import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSquare } from "@fortawesome/free-solid-svg-icons";
import "./transaction.css";
import { useEffect, useState } from "react";
import InforBoard from "../inforBoard/inforBoard";

const Transactions = ({ full }) => {
  const [list, setList] = useState([]);
  useEffect(() => {
    fetch("http://localhost:5000/admin/get-transactions")
      .then((reponse) => {
        return reponse.json();
      })
      .then((reponse) => {
        const arr = full ? reponse.arr : reponse.arr.slice(0, 8);
        setList(arr);

        // setMaxPage(reponse.maxPage);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [full]);
  return (
    <div className="col-10 main">
      <InforBoard />
      <div className="transactions-box">
        <h1>Lastest Transactions</h1>
        <div className="t-box">
          <table className="table">
            <thead>
              <tr>
                <th scope="col">
                  <FontAwesomeIcon icon={faSquare} />
                </th>
                <th scope="col">id</th>
                <th scope="col">User</th>
                <th scope="col">Hotel</th>
                <th scope="col">Room</th>
                <th scope="col">Date</th>
                <th scope="col">Price</th>
                <th scope="col">Payment Method</th>
                <th scope="col">Status</th>
              </tr>
            </thead>
            <tbody>
              {list.map((t) => {
                return (
                  <tr key={t._id}>
                    <th scope="col">
                      <FontAwesomeIcon icon={faSquare} />
                    </th>
                    <td>{t._id}</td>
                    <td>{t.idUser.username}</td>
                    <td>{t.idHotel.name}</td>
                    <td>
                      {t.room.map((r) => {
                        return r + " ";
                      })}
                    </td>
                    <td>
                      {t.dateStart}-<br></br>
                      {t.dateEnd}
                    </td>
                    <td>{t.price}</td>
                    <td>{t.payment}</td>
                    <td>
                      <div className={`status-box-${t.status}`}>{t.status}</div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
          <div className="space"></div>
          <div className="page">
            <div>
              <p>
                1-{list.length} of {list.length} {"< >"}
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Transactions;
