import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUser,
  faCartArrowDown,
  faMoneyBillAlt,
  faWallet,
  faSquare,
} from "@fortawesome/free-solid-svg-icons";
import "./transaction.css";
import { useEffect, useState } from "react";

const Transactions = () => {
  const trans = 8;
  const [page, setPage] = useState(1);
  const [list, setList] = useState([]);
  const [maxPage, setMaxPage] = useState(1);
  useEffect(() => {
    fetch("http://localhost:5000/admin/get-transactions", {
      method: "POST",
      body: JSON.stringify({ page: page, trans: trans }),
      headers: { "Content-Type": "application/json" },
      credentials: "same-origin",
    })
      .then((reponse) => {
        return reponse.json();
      })
      .then((reponse) => {
        setList(reponse.arr);
        setMaxPage(reponse.maxPage);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [page]);
  return (
    <div className="col-10 main">
      <div className="row">
        <div className="col-3">
          <div className="box">
            <p>USERS</p>
            <p>100</p>
            <div style={{ backgroundColor: "#ffcccc" }}>
              <FontAwesomeIcon icon={faUser} />
            </div>
          </div>
        </div>
        <div className="col-3">
          <div className="box">
            <p>ORDERS</p>
            <p>100</p>
            <div style={{ backgroundColor: "#F8EDD2" }}>
              <FontAwesomeIcon icon={faCartArrowDown} />
            </div>
          </div>
        </div>
        <div className="col-3">
          <div className="box">
            <p>EARNINGS</p>
            <p>100</p>
            <div style={{ backgroundColor: "#CCE6CC" }}>
              <FontAwesomeIcon icon={faMoneyBillAlt} />
            </div>
          </div>
        </div>
        <div className="col-3">
          <div className="box">
            <p>BLANCE</p>
            <p>100</p>
            <div style={{ backgroundColor: "#E6CCE6" }}>
              <FontAwesomeIcon icon={faWallet} />
            </div>
          </div>
        </div>
      </div>
      <div className="transactions-box">
        <h1>Lastest Transactions</h1>
        <div className="t-box">
          <table className="table">
            <thead>
              <tr>
                <th scope="col">
                  <FontAwesomeIcon icon={faSquare} />
                </th>
                <th scope="col">id</th>
                <th scope="col">User</th>
                <th scope="col">Hotel</th>
                <th scope="col">Room</th>
                <th scope="col">Date</th>
                <th scope="col">Price</th>
                <th scope="col">Payment Method</th>
                <th scope="col">Status</th>
              </tr>
            </thead>
            <tbody>
              {list.map((t) => {
                return (
                  <tr key={t._id}>
                    <th scope="col">
                      <FontAwesomeIcon icon={faSquare} />
                    </th>
                    <td>{t._id}</td>
                    <td>{t.idUser.username}</td>
                    <td>{t.idHotel.name}</td>
                    <td>
                      {t.room.map((r) => {
                        return r + " ";
                      })}
                    </td>
                    <td>
                      {t.dateStart}-<br></br>
                      {t.dateEnd}
                    </td>
                    <td>{t.price}</td>
                    <td>{t.payment}</td>
                    <td>
                      <div className={`status-box-${t.status}`}>{t.status}</div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
          <div className="space"></div>
          <div className="page">
            <div>
              <p>
                {page}-{maxPage}
              </p>
              {page > 1 ? (
                <button
                  onClick={() => {
                    setPage(page - 1);
                  }}
                >
                  {"<"}
                </button>
              ) : (
                <div></div>
              )}
              {page < maxPage ? (
                <button
                  onClick={() => {
                    setPage(page + 1);
                  }}
                >
                  {">"}
                </button>
              ) : (
                <div></div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Transactions;
