//import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
//import { faSquare } from '@fortawesome/free-solid-svg-icons';
import './products.css';
import { useEffect, useState } from 'react';
import AdminAPI from '../../API/AdminAPI';
import convertMoney from '../../convertMoney';
import { Navigate } from 'react-router-dom';
import axios from 'axios';

const Products = () => {
  // let navigate = useNavigate();
  const [check, setCheck] = useState(false);
  const [list, setList] = useState([]);
  const [redirect, setRedirect] = useState(false);
  // const handleDelete = (t) => {
  //   if (!window.confirm("Do you want delete this Hotel?")) return;
  //   fetch("http://localhost:5000/admin/delete-hotel", {
  //     method: "POST",
  //     body: JSON.stringify({ id: t._id }),
  //     headers: { "Content-Type": "application/json" },
  //     credentials: "same-origin",
  //   })
  //     .then((reponse) => {
  //       return reponse.json();
  //     })
  //     .then((reponse) => {
  //       if (reponse.check) {
  //         alert("Successfull!");
  //         setCheck(!check);
  //       } else {
  //         alert("Can not delete this Hotel!");
  //       }
  //     })
  //     .catch((err) => {
  //       console.log(err);
  //     });
  // };
  useEffect(() => {
    console.log(list.length);
    const fetchData = async () => {
      const response = await AdminAPI.getProduces();
      // console.log(response.data.products);
      if (response.data.error) {
        setRedirect(true);
      } else {
        setList(response.data.products);
        console.log(list);
      }
    };
    fetchData();
  }, [check]);

  if (list.length === 0) {
    return <div></div>;
  }
  return (
    <div className="col-10 main">
      <div className="transactions-box">
        <div className="btn-add">
          {/* <h1>Hotels List</h1> */}
          {/* <button
            onClick={() => {
              navigate('/new-hotel');
            }}
          >
            Add New
          </button> */}
        </div>
        <div className="t-box">
          <table className="table">
            <thead>
              <tr>
                {/* <th scope="col">
                  <FontAwesomeIcon icon={faSquare} />
                </th> */}
                <th scope="col">id</th>
                <th scope="col">Name</th>
                <th scope="col">Price</th>
                <th scope="col">Image</th>
                <th scope="col">Category</th>
                <th scope="col">Edit</th>
              </tr>
            </thead>
            <tbody>
              {list.map((t) => {
                return (
                  <tr key={t._id} className={`row-${list.indexOf(t) % 2}`}>
                    {/* <th scope="col">
                      <FontAwesomeIcon icon={faSquare} />
                    </th> */}
                    <td>{t._id} </td>
                    <td>
                      {t.name} {list.indexOf(t)}
                    </td>
                    <td>{convertMoney(t.price)} VND</td>
                    <td>
                      <img src={t.img1}></img>
                    </td>
                    <td>{t.category}</td>
                    <td>
                      <div className="btn-b">
                        <button className="btn-edit">Update</button>
                        <button className="btn-delete">Delete</button>
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
          <div className="space"></div>
          {/* <div className="page">
            <div>
              <p>
                1-{list.length} of {list.length} {"< >"}
              </p>
            </div>
          </div> */}
        </div>
      </div>
    </div>
  );
};

export default Products;
