import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSquare } from "@fortawesome/free-solid-svg-icons";
import "./rooms.css";
import { useEffect, useState } from "react";

const Rooms = () => {
  const trans = 8;
  const [page, setPage] = useState(1);
  const [list, setList] = useState([]);
  const [maxPage, setMaxPage] = useState(1);
  useEffect(() => {
    fetch("http://localhost:5000/admin/get-rooms", {
      method: "POST",
      body: JSON.stringify({ page: page, trans: trans }),
      headers: { "Content-Type": "application/json" },
      credentials: "same-origin",
    })
      .then((reponse) => {
        return reponse.json();
      })
      .then((reponse) => {
        setList(reponse.arr);
        setMaxPage(reponse.maxPage);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [page]);
  return (
    <div className="col-10 main">
      <div className="transactions-box">
        <div className="btn-add">
          <h1>Hotels List</h1>
        </div>
        <div className="t-box">
          <table className="table">
            <thead>
              <tr>
                <th scope="col">
                  <FontAwesomeIcon icon={faSquare} />
                </th>
                <th scope="col">id</th>
                <th scope="col">Room Number</th>
                <th scope="col">Max People</th>
                <th scope="col">Price</th>
                <th scope="col">Title</th>
                <th scope="col">Description</th>
                <th scope="col">Action</th>
              </tr>
            </thead>
            <tbody>
              {list.map((t) => {
                return (
                  <tr key={t._id}>
                    <th scope="col">
                      <FontAwesomeIcon icon={faSquare} />
                    </th>
                    <td>{t._id}</td>
                    <td>
                      {t.roomNumbers.map((r) => {
                        return r + " ";
                      })}
                    </td>
                    <td>{t.maxPeople}</td>
                    <td>{t.price}</td>
                    <td>{t.title}</td>
                    <td>{t.desc}</td>
                    <td>
                      <button className="btn-delete">Delete</button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
          <div className="space"></div>
          <div className="page">
            <div>
              <p>
                {page}-{maxPage}
              </p>
              {page > 1 ? (
                <button
                  onClick={() => {
                    setPage(page - 1);
                  }}
                >
                  {"<"}
                </button>
              ) : (
                <div></div>
              )}
              {page < maxPage ? (
                <button
                  onClick={() => {
                    setPage(page + 1);
                  }}
                >
                  {">"}
                </button>
              ) : (
                <div></div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Rooms;
