import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSquare } from "@fortawesome/free-solid-svg-icons";
import "./rooms.css";
import { useEffect, useState } from "react";

const Rooms = () => {
  const [list, setList] = useState([]);
  const [check, setCheck] = useState(false);
  const handleDelete = (t) => {
    if (!window.confirm("Do you want delete this Hotel?")) return;
    fetch("http://localhost:5000/admin/delete-room", {
      method: "POST",
      body: JSON.stringify({ id: t._id }),
      headers: { "Content-Type": "application/json" },
      credentials: "same-origin",
    })
      .then((reponse) => {
        return reponse.json();
      })
      .then((reponse) => {
        if (reponse.check) {
          alert("Successfull!");
          setCheck(!check);
        } else {
          alert("Can not delete this Hotel!");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };
  useEffect(() => {
    fetch("http://localhost:5000/admin/get-rooms")
      .then((reponse) => {
        return reponse.json();
      })
      .then((reponse) => {
        setList(reponse.arr);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [check]);
  return (
    <div className="col-10 main">
      <div className="transactions-box">
        <div className="btn-add">
          <h1>Rooms List</h1>
        </div>
        <div className="t-box">
          <table className="table">
            <thead>
              <tr>
                <th scope="col">
                  <FontAwesomeIcon icon={faSquare} />
                </th>
                <th scope="col">id</th>
                <th scope="col">Room Number</th>
                <th scope="col">Max People</th>
                <th scope="col">Price</th>
                <th scope="col">Title</th>
                <th scope="col">Description</th>
                <th scope="col">Action</th>
              </tr>
            </thead>
            <tbody>
              {list.map((t) => {
                return (
                  <tr key={t._id}>
                    <th scope="col">
                      <FontAwesomeIcon icon={faSquare} />
                    </th>
                    <td>{t._id}</td>
                    <td>
                      {t.roomNumbers.map((r) => {
                        return r + " ";
                      })}
                    </td>
                    <td>{t.maxPeople}</td>
                    <td>{t.price}</td>
                    <td>{t.title}</td>
                    <td>{t.desc}</td>
                    <td>
                      <button
                        className="btn-delete"
                        onClick={() => handleDelete(t)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
          <div className="space"></div>
          <div className="page">
            <div>
              <p>
                1-{list.length} of {list.length} {"< >"}
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Rooms;
