import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSquare } from "@fortawesome/free-solid-svg-icons";
import "./hotels.css";
import { useEffect, useState } from "react";
import { Navigate } from "react-router-dom";

const Hotel = () => {
  const [redirect, setRedirect] = useState(false);
  const [check, setCheck] = useState(false);
  const [list, setList] = useState([]);
  const handleDelete = (t) => {
    if (!window.confirm("Do you want delete this Hotel?")) return;
    fetch("http://localhost:5000/admin/delete-hotel", {
      method: "POST",
      body: JSON.stringify({ id: t._id }),
      headers: { "Content-Type": "application/json" },
      credentials: "same-origin",
    })
      .then((reponse) => {
        return reponse.json();
      })
      .then((reponse) => {
        if (reponse.check) {
          alert("Successfull!");
          setCheck(!check);
        } else {
          alert("Can not delete this Hotel!");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };
  useEffect(() => {
    fetch("http://localhost:5000/admin/get-hotelsFull")
      .then((reponse) => {
        return reponse.json();
      })
      .then((reponse) => {
        setList(reponse.arr);
        // setMaxPage(reponse.maxPage);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [check]);
  if (redirect) {
    return <Navigate to={`/new-hotel`} />;
  }
  return (
    <div className="col-10 main">
      <div className="transactions-box">
        <div className="btn-add">
          <h1>Hotels List</h1>
          <button
            onClick={() => {
              setRedirect(true);
            }}
          >
            Add New
          </button>
        </div>
        <div className="t-box">
          <table className="table">
            <thead>
              <tr>
                <th scope="col">
                  <FontAwesomeIcon icon={faSquare} />
                </th>
                <th scope="col">id</th>
                <th scope="col">Name</th>
                <th scope="col">Type</th>
                <th scope="col">Title</th>
                <th scope="col">City</th>
                <th scope="col">Action</th>
              </tr>
            </thead>
            <tbody>
              {list.map((t) => {
                return (
                  <tr key={t._id}>
                    <th scope="col">
                      <FontAwesomeIcon icon={faSquare} />
                    </th>
                    <td>{t._id}</td>
                    <td>{t.name}</td>
                    <td>{t.type}</td>
                    <td>{t.title}</td>
                    <td>{t.city}</td>
                    <td>
                      <button
                        className="btn-delete"
                        onClick={() => handleDelete(t)}
                      >
                        Delete
                      </button>
                      <button className="btn-edit">Edit</button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
          <div className="space"></div>
          {/* <div className="page">
            <div>
              <p>
                {page}-{maxPage}
              </p>
              {page > 1 ? (
                <button
                  onClick={() => {
                    setPage(page - 1);
                  }}
                >
                  {"<"}
                </button>
              ) : (
                <div></div>
              )}
              {page < maxPage ? (
                <button
                  onClick={() => {
                    setPage(page + 1);
                  }}
                >
                  {">"}
                </button>
              ) : (
                <div></div>
              )}
            </div>
          </div> */}
        </div>
      </div>
    </div>
  );
};

export default Hotel;
