import { useEffect } from "react";
import { useState } from "react";
import { Navigate, useParams, useNavigate } from "react-router-dom";
import "./newHotel.css";

const NewHotel = ({ addCheck }) => {
  let navigate = useNavigate();
  const [hotel, setHotel] = useState(null);
  const { idHotel } = useParams();
  const [profile, setProfile] = useState({
    name: "",
    type: "",
    city: "",
    address: "",
    distance: "",
    title: "",
    desc: "",
    cheapestPrice: "",
    photos: "",
    featured: false,
    rooms: "",
  });
  const validate = () => {
    if (profile.name === "") {
      return { ok: false, message: "Please fill in name!!" };
    } else if (profile.type === "") {
      return { ok: false, message: "Please fill in type!!" };
    } else if (profile.city === "") {
      return { ok: false, message: "Please fill in city!!" };
    } else if (profile.address === "") {
      return { ok: false, message: "Please fill in address!!" };
    } else if (profile.distance === "") {
      return { ok: false, message: "Please fill in distance!!" };
    } else if (profile.title === "") {
      return { ok: false, message: "Please fill in title!!" };
    } else if (profile.cheapestPrice === "") {
      return { ok: false, message: "Please fill in price!!" };
    } else if (profile.photos === "") {
      return { ok: false, message: "Please fill in images!!" };
    } else if (profile.rooms === "") {
      return { ok: false, message: "Please fill in rooms!!" };
    } else {
      return { ok: true };
    }
  };
  const handleSubmit = () => {
    const vadidateForm = validate();
    if (!vadidateForm.ok) {
      alert(vadidateForm.message);
    } else {
      fetch("http://localhost:5000/admin/add-hotel", {
        method: "POST",
        body: JSON.stringify(profile),
        headers: { "Content-Type": "application/json" },
        credentials: "same-origin",
      })
        .then((reponse) => {
          return reponse.json();
        })
        .then((reponse) => {
          if (reponse.ok) {
            alert("Successfull!");
            setProfile({
              name: "",
              type: "",
              city: "",
              address: "",
              distance: "",
              title: "",
              desc: "",
              cheapestPrice: "",
              photos: "",
              featured: false,
              rooms: "",
            });
          }
        })
        .catch((err) => console.log(err));
    }
  };
  useEffect(() => {
    if (!addCheck) {
      fetch("http://localhost:5000/admin/get-hotel", {
        method: "POST",
        body: JSON.stringify({ id: idHotel }),
        headers: { "Content-Type": "application/json" },
        credentials: "same-origin",
      })
        .then((reponse) => {
          return reponse.json();
        })
        .then((reponse) => {
          if (!reponse.check) {
            alert("Can not edit this hotel!!");
            navigate("/hotel");
          } else {
            setHotel(reponse.arr);
            console.log(reponse.arr);
          }
        })
        .catch((err) => {
          console.log(err);
        });
    }
  }, []);
  return (
    <div className="col-10 main">
      <div className="title">Add New Product</div>
      <div className="form-add row">
        <div className="col-6 input">
          <label htmlFor="name">Name</label>
          <input
            id="name"
            name="name"
            type="text"
            placeholder="My Hotel"
            value={profile.name}
            onChange={(evt) => {
              setProfile({ ...profile, name: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="type">Type</label>
          <input
            id="type"
            name="type"
            type="text"
            placeholder="hotel"
            value={profile.type}
            onChange={(evt) => {
              setProfile({ ...profile, type: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="city">City</label>
          <input
            id="city"
            name="city"
            type="text"
            placeholder="New York"
            value={profile.city}
            onChange={(evt) => {
              setProfile({ ...profile, city: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="adress">Address</label>
          <input
            id="adress"
            name="adress"
            type="text"
            placeholder="elton st, 216"
            value={profile.address}
            onChange={(evt) => {
              setProfile({ ...profile, address: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="distance">Distance from City Center</label>
          <input
            id="distance"
            name="distance"
            type="distance"
            value={profile.distance}
            placeholder="500"
            onChange={(evt) => {
              setProfile({ ...profile, distance: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="title">Title</label>
          <input
            id="title"
            name="title"
            type="text"
            placeholder="The best Hotel"
            value={profile.title}
            onChange={(evt) => {
              setProfile({ ...profile, title: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="desc">Description</label>
          <input
            id="desc"
            name="desc"
            type="text"
            placeholder="description"
            value={profile.desc}
            onChange={(evt) => {
              setProfile({ ...profile, desc: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="price">Price</label>
          <input
            id="price"
            name="price"
            type="text"
            placeholder="100"
            value={profile.cheapestPrice}
            onChange={(evt) => {
              setProfile({ ...profile, cheapestPrice: evt.target.value });
            }}
          ></input>
        </div>
        <div className="col-6 input">
          <label htmlFor="images">Images</label>
          <textarea
            id="images"
            name="images"
            value={profile.photos}
            onChange={(evt) => {
              setProfile({ ...profile, photos: evt.target.value });
            }}
          ></textarea>
        </div>
        <div className="col-6 input">
          <label htmlFor="featured">Featured</label>
          <select
            onChange={(evt) => {
              if (evt.target.value === "Yes") {
                setProfile({ ...profile, featured: true });
              } else {
                setProfile({ ...profile, featured: false });
              }
            }}
          >
            <option>Yes</option>
            <option>No</option>
          </select>
        </div>
        <div className="col-12 input">
          <label htmlFor="rooms">Rooms</label>
          <textarea
            id="rooms"
            name="rooms"
            value={profile.rooms}
            onChange={(evt) => {
              setProfile({ ...profile, rooms: evt.target.value });
            }}
          ></textarea>
        </div>
        <div className="button-send">
          {addCheck ? (
            <button type="submit" onClick={handleSubmit}>
              Send
            </button>
          ) : (
            <button type="submit">Save</button>
          )}
        </div>
      </div>
    </div>
  );
};

export default NewHotel;
